                  category = 'property';
                } else if (filename.includes('license') || filename.includes('llc') || filename.includes('id')) {
                  category = 'borrower';
                } else if (filename.includes('loan') || filename.includes('application')) {
                  category = 'loan';
                }



                // Save document to database with local file reference
