                  name: attachment.filename,
                  fileId: fileId, // Use local filename for "Send to Drive" functionality
                  loanId: loanId,
                  fileType: attachment.mimeType,
                  fileSize: attachment.size,
                  category: category,
                  source: `gmail:${message.from}`,
