import { Document, Contact } from "@/lib/types";
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

import SendToAnalyst from "@/components/SendToAnalyst";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { format } from "date-fns";
import { Loader2, FileText, Image, File, Download, Trash2, Eye, Check, Plus, X, Upload, RotateCcw } from "lucide-react";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle } from "@/components/ui/alert-dialog";



interface DocumentManagerProps {
  documents: Document[];
  loanId: number;
  loanNumber: string;
  contacts: Contact[];
  propertyAddress: string;
  requiredDocuments: {
    borrower: string[];
    property: string[];
    title: string[];
    insurance: string[];
  };
  completedRequirements?: Set<string>;
  onCompletedRequirementsChange?: (completed: Set<string>) => void;
}

export default function DocumentManager({ 
  documents, 
  loanId, 
  loanNumber,
  contacts, 
  propertyAddress, 
  requiredDocuments, 
  completedRequirements: externalCompletedRequirements,
  onCompletedRequirementsChange 
}: DocumentManagerProps) {
  const [activeTab, setActiveTab] = useState("document-list");

  const [localCompletedRequirements, setLocalCompletedRequirements] = useState<Set<string>>(new Set());
  const [assignedDocuments, setAssignedDocuments] = useState<Record<string, string[]>>({}); // requirement -> document IDs
  const [customDocuments, setCustomDocuments] = useState<Array<{name: string, category: string}>>([]); // Custom missing documents
  const [searchQuery, setSearchQuery] = useState("");
  const [deletedDocuments, setDeletedDocuments] = useState<Document[]>([]);
  const [showDeleted, setShowDeleted] = useState(false);
  const [newCustomDocumentName, setNewCustomDocumentName] = useState("");
  const [showAddCustomDocument, setShowAddCustomDocument] = useState(false);
  const [showInlineUpload, setShowInlineUpload] = useState<string | null>(null); // Track which requirement is showing upload
  const [showResetConfirmation, setShowResetConfirmation] = useState(false);
  const [isResetting, setIsResetting] = useState(false);
  const [isSendingToDrive, setIsSendingToDrive] = useState(false);
  const [driveFolder, setDriveFolder] = useState<string | null>(null);
  const { toast } = useToast();

  // Use external completed requirements if provided, otherwise use local state
  const completedRequirements = externalCompletedRequirements || localCompletedRequirements;

  // Load document assignments and check Google Drive status when component mounts
  useEffect(() => {
    const loadLoanData = async () => {
      try {
        const response = await fetch(`/api/loans/${loanId}`);
        const data = await response.json();
        console.log('Loaded loan data:', data);

        if (data.loan?.documentAssignments) {
          console.log('Setting document assignments:', data.loan.documentAssignments);
          setAssignedDocuments(data.loan.documentAssignments);
        } else {
          console.log('No document assignments found in loan data');
        }

        // Set drive folder from loan data
        if (data.loan?.googleDriveFolderId || data.loan?.driveFolder) {
          setDriveFolder(data.loan.googleDriveFolderId || data.loan.driveFolder);
        }
      } catch (error) {
        console.error("Failed to load document assignments:", error);
      }
    };

    loadLoanData();
  }, [loanId]);

  const sendToDrive = async () => {
    console.log("SendToDrive button clicked!", { driveFolder, loanId });

    if (!driveFolder) {
      toast({
        title: "No Drive Folder",
        description: "No Google Drive folder is connected to this loan.",
        variant: "destructive",
      });
      return;
    }

    setIsSendingToDrive(true);
    try {
      console.log("Making POST request to send-to-drive...");
      const response = await fetch(`/api/loans/${loanId}/send-to-drive`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          folderId: driveFolder
        }),
      });

      console.log("Response status:", response.status);
      if (!response.ok) {
        throw new Error('Failed to send documents to Drive');
      }

      const result = await response.json();
      console.log("Send to drive result:", result);

      toast({
        title: "Success",
        description: `${result.uploadedCount} documents sent to Google Drive`,
      });
    } catch (error) {
      console.error('Error sending to Drive:', error);
      toast({
        title: "Error",
        description: "Failed to send documents to Google Drive",
        variant: "destructive",
      });
    } finally {
      setIsSendingToDrive(false);
    }
  };





  const resetAllDocuments = async () => {
    setIsResetting(true);
    try {
      const response = await fetch(`/api/loans/${loanId}/reset-documents`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Reset failed');
      }

      const result = await response.json();

      // Clear local state
      setAssignedDocuments({});
      setDeletedDocuments([]);

      // Refresh data
      queryClient.invalidateQueries({ queryKey: [`/api/loans/${loanId}`] });
      queryClient.invalidateQueries({ queryKey: ['/api/loans', loanId, 'documents'] });

      toast({
        title: "Success",
        description: "All documents have been permanently deleted from both active and deleted sections."
      });

      setShowResetConfirmation(false);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to reset documents. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsResetting(false);
    }
  };

  const fetchDeletedDocuments = async () => {
    try {
      const response = await fetch(`/api/loans/${loanId}/deleted-documents`);
      if (response.ok) {
        const deleted = await response.json();
        setDeletedDocuments(deleted);
      }
    } catch (error) {
      console.error('Failed to fetch deleted documents:', error);
    }
  };

  useEffect(() => {
    fetchDeletedDocuments();
  }, [loanId]);

  const restoreDocument = async (documentId: number) => {
    try {
      const response = await apiRequest("PATCH", `/api/documents/${documentId}/restore`, {});
      toast({
        title: "Success",
        description: "Document restored successfully."
      });
      // Refresh both document lists and deleted documents
      queryClient.invalidateQueries({ queryKey: [`/api/loans/${loanId}`] });
      fetchDeletedDocuments();
    } catch (error) {
      console.error('Restore error:', error);
      toast({
        title: "Error",
        description: "Failed to restore document.",
        variant: "destructive"
      });
    }
  };

  // Helper functions for managing requirements
  const assignDocumentToRequirement = async (requirementName: string, documentId: string) => {
    const newAssignments = {
      ...assignedDocuments,
      [requirementName]: [...(assignedDocuments[requirementName] || []), documentId]
    };
    setAssignedDocuments(newAssignments);

    // Persist to database
    try {
      await apiRequest("PATCH", `/api/loans/${loanId}/document-assignments`, {
        documentAssignments: newAssignments
      });
    } catch (error) {
      console.error("Failed to save document assignments:", error);
    }
  };

  const removeDocumentFromRequirement = async (requirementName: string, documentId: string) => {
    const newAssignments = {
      ...assignedDocuments,
      [requirementName]: (assignedDocuments[requirementName] || []).filter(id => id !== documentId)
    };
    setAssignedDocuments(newAssignments);

    // Persist to database
    try {
      await apiRequest("PATCH", `/api/loans/${loanId}/document-assignments`, {
        documentAssignments: newAssignments
      });
    } catch (error) {
      console.error("Failed to save document assignments:", error);
    }
  };

  const markRequirementComplete = (requirementName: string) => {
    const newCompleted = new Set(Array.from(completedRequirements).concat(requirementName));
    if (onCompletedRequirementsChange) {
      onCompletedRequirementsChange(newCompleted);
    } else {
      setLocalCompletedRequirements(newCompleted);
    }
    toast({
      title: "Requirement Completed",
      description: `"${requirementName}" has been marked as complete.`
    });
  };

  const unmarkRequirementComplete = (requirementName: string) => {
    const newCompleted = new Set(completedRequirements);
    newCompleted.delete(requirementName);
    if (onCompletedRequirementsChange) {
      onCompletedRequirementsChange(newCompleted);
    } else {
      setLocalCompletedRequirements(newCompleted);
    }
  };

  // Functions for custom missing documents
  const addCustomDocument = () => {
    if (newCustomDocumentName.trim()) {
      const newCustomDoc = {
        name: newCustomDocumentName.trim(),
        category: "custom"
      };
      setCustomDocuments(prev => [...prev, newCustomDoc]);
      setNewCustomDocumentName("");
      setShowAddCustomDocument(false);
      toast({
        title: "Custom Document Added",
        description: `"${newCustomDoc.name}" has been added to missing documents.`
      });
    }
  };

  const removeCustomDocument = (documentName: string) => {
    setCustomDocuments(prev => prev.filter(doc => doc.name !== documentName));
    // Also remove from completed requirements if it was marked complete
    const newCompleted = new Set(completedRequirements);
    newCompleted.delete(documentName);
    if (onCompletedRequirementsChange) {
      onCompletedRequirementsChange(newCompleted);
    } else {
      setLocalCompletedRequirements(newCompleted);
    }
    toast({
      title: "Custom Document Removed",
      description: `"${documentName}" has been removed from missing documents.`
    });
  };

  // Calculate missing and completed documents
  const allRequirements = [
    ...requiredDocuments.borrower.map(doc => ({ name: doc, category: "borrower" })),
    ...requiredDocuments.property.map(doc => ({ name: doc, category: "property" })),
    ...requiredDocuments.title.map(doc => ({ name: doc, category: "title" })),
    ...requiredDocuments.insurance.map(doc => ({ name: doc, category: "insurance" })),
    ...customDocuments // Include custom documents in the list
  ];

  const missingDocuments = allRequirements.filter(req => !completedRequirements.has(req.name));
  const completedDocuments = allRequirements.filter(req => completedRequirements.has(req.name));

  // Handle document preview
  const handleDocumentPreview = async (document: Document) => {
    try {
      console.log('Previewing document:', document);
      const response = await fetch(`/api/documents/${document.id}/view`);
      const data = await response.json();
      console.log('Preview response:', data);

      if (data.type === 'drive' && data.viewUrl) {
        console.log('Opening Google Drive URL:', data.viewUrl);
        window.open(data.viewUrl, '_blank');
      } else if (data.type === 'upload' && data.fileUrl) {
        console.log('Opening upload URL:', data.fileUrl);
        window.open(data.fileUrl, '_blank');
      } else {
        console.error('Invalid preview data:', data);
        toast({
          title: "Error",
          description: "Invalid document preview data",
          variant: "destructive"
        });
      }
    } catch (error) {
      console.error('Preview error:', error);
      toast({
        title: "Error",
        description: "Failed to preview document",
        variant: "destructive"
      });
    }
  };

  const getFileIcon = (document: Document) => {
    if (document.fileType?.includes('image')) {
      return <Image className="w-4 h-4 text-blue-500" />;
    } else if (document.fileType?.includes('pdf')) {
      return <FileText className="w-4 h-4 text-red-500" />;
    }
    return <File className="w-4 h-4 text-gray-500" />;
  };

  const viewDocument = async (doc: Document) => {
    try {
      // Call the view endpoint to determine document type and get view URL
      const response = await apiRequest("GET", `/api/documents/${doc.id}/view`);

      if (response.type === 'drive') {
        // Open Google Drive document in new tab
        window.open(response.viewUrl, '_blank');
      } else if (response.type === 'upload') {
        // For uploaded documents, open the file directly
        window.open(response.fileUrl, '_blank');
      }
    } catch (error) {
      console.error("View document error:", error);
      toast({
        title: "Error",
        description: "Failed to open document.",
        variant: "destructive"
      });
    }
  };

  const downloadDocument = async (doc: Document) => {
    try {
      const response = await apiRequest("GET", `/api/documents/${doc.id}/download`);
      if (response.downloadUrl) {
        // Create a temporary link to trigger download
        const link = document.createElement('a');
        link.href = response.downloadUrl;
        link.download = doc.name;
        link.target = '_blank';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);

        toast({
          title: "Success",
          description: "Download started successfully."
        });
      } else {
        throw new Error('Download URL not available');
      }
    } catch (error) {
      console.error("Download error:", error);
      toast({
        title: "Error",
        description: "Failed to download document.",
        variant: "destructive"
      });
    }
  };

  const deleteDocument = async (docId: number) => {
    try {
      const response = await apiRequest("DELETE", `/api/documents/${docId}`);
      if (response.success) {
        // Refresh both the main loan data and deleted documents immediately
        queryClient.invalidateQueries({ queryKey: [`/api/loans/${loanId}`] });
        fetchDeletedDocuments();
        toast({
          title: "Success",
          description: "Document deleted successfully."
        });
      } else {
        throw new Error('Delete failed');
      }
    } catch (error) {
      console.error("Delete error:", error);
      toast({
        title: "Error",
        description: "Failed to delete document.",
        variant: "destructive"
      });
    }
  };



  return (
    <>
      <div className="space-y-6">
        <div className="flex justify-between items-center">
          <h3 className="text-lg font-semibold">Document Management</h3>
          <div className="flex gap-2">
            <SendToAnalyst 
              documents={documents}
              contacts={contacts}
              loanId={loanId}
              loanNumber={loanNumber}
              propertyAddress={propertyAddress}
              documentAssignments={assignedDocuments}
              completedRequirements={Array.from(completedRequirements)}
            />

            {driveFolder && (
              <Button
                onClick={sendToDrive}
                disabled={isSendingToDrive}
                variant="outline"
                size="sm"
                className="text-blue-600 hover:text-blue-700 border-blue-300 hover:border-blue-400"
              >
                {isSendingToDrive ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Sending...
                  </>
                ) : (
                  <>
                    <Upload className="w-4 h-4 mr-2" />
                    Send to Drive
                  </>
                )}
              </Button>
            )}

            <Button
              onClick={() => setShowResetConfirmation(true)}
              disabled={isResetting}
              variant="outline"
              size="sm"
              className="text-red-600 hover:text-red-700 border-red-300 hover:border-red-400"
            >
              {isResetting ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Resetting...
                </>
              ) : (
                <>
                  <RotateCcw className="w-4 h-4 mr-2" />
                  Reset Documents
                </>
              )}
            </Button>
          </div>
        </div>



        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="document-list">
              All Documents ({documents.length})
            </TabsTrigger>
            <TabsTrigger value="missing">
              Missing ({missingDocuments.length})
            </TabsTrigger>
            <TabsTrigger value="upload">
              Upload
            </TabsTrigger>
            <TabsTrigger value="completed">
              Completed ({completedDocuments.length})
            </TabsTrigger>
          </TabsList>

          <TabsContent value="document-list" className="space-y-4">
            {documents.length === 0 ? (
              <Card>
                <CardContent className="pt-6">
                  <p className="text-center text-gray-500">
                    No documents uploaded yet. Use the Upload tab to add documents.
                  </p>
                </CardContent>
              </Card>
            ) : (
              <>
                <div className="flex items-center gap-2 mb-4">
                  <Input
                    placeholder="Search documents by name..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="max-w-sm"
                  />
                  {searchQuery && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setSearchQuery("")}
                    >
                      <X className="w-4 h-4" />
                    </Button>
                  )}
                </div>
                <div className="grid gap-4">
                  {documents
                    .filter(doc => 
                      doc.name.toLowerCase().includes(searchQuery.toLowerCase())
                    )
                    .sort((a, b) => new Date(b.uploadedAt || 0).getTime() - new Date(a.uploadedAt || 0).getTime())
                    .map((doc) => (
                      <Card key={doc.id} className="p-4">
                          <div className="flex items-center justify-between">
                            <div className="flex items-center gap-3">
                              {getFileIcon(doc)}
                              <div>
                                <p className="font-medium">{doc.name}</p>
                                <div className="flex items-center gap-2 text-sm text-gray-500">
                                  {doc.category && (
                                    <Badge variant="outline" className="text-xs">
                                      {doc.category}
                                    </Badge>
                                  )}
                                  {doc.uploadedAt && (
                                    <span>
                                      {format(new Date(doc.uploadedAt), "MMM dd, yyyy 'at' h:mm a")}
                                    </span>
                                  )}
                                  {doc.fileSize && (
                                    <span>
                                      {doc.fileSize >= 1024 * 1024 
                                        ? `${(doc.fileSize / 1024 / 1024).toFixed(1)} MB`
                                        : `${Math.round(doc.fileSize / 1024)} KB`
                                      }
                                    </span>
                                  )}
                                </div>

                              </div>
                            </div>
                            <div className="flex items-center gap-2">
                              <Button 
                                size="sm" 
                                variant="ghost"
                                onClick={() => viewDocument(doc)}
                                title="View document"
                              >
                                <Eye className="w-4 h-4" />
                              </Button>
                              <Button 
                                size="sm" 
                                variant="ghost"
                                onClick={() => downloadDocument(doc)}
                                title="Download document"
                              >
                                <Download className="w-4 h-4" />
                              </Button>
                              <Button 
                                size="sm" 
                                variant="ghost" 
                                onClick={() => deleteDocument(doc.id)}
                                title="Delete document"
                              >
                                <Trash2 className="w-4 h-4" />
                              </Button>
                            </div>
                          </div>
                  </Card>
                ))}
                </div>


              </>
            )}
          </TabsContent>

          <TabsContent value="missing" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Missing Documents</CardTitle>
                <CardDescription>
                  These documents are required but haven't been completed yet.
                </CardDescription>
              </CardHeader>
              <CardContent>
                {/* Add Custom Document Section */}
                <div className="mb-6 p-4 bg-gray-50 rounded-lg border">
                  <div className="flex items-center justify-between mb-3">
                    <h4 className="font-medium text-gray-900">Add Custom Requirement</h4>
                    {!showAddCustomDocument && (
                      <Button 
                        size="sm" 
                        variant="outline"
                        onClick={() => setShowAddCustomDocument(true)}
                      >
                        <Plus className="w-4 h-4 mr-1" />
                        Add Custom Requirement
                      </Button>
                    )}
                  </div>

                  {showAddCustomDocument && (
                    <div className="flex items-center gap-2">
                      <Input
                        placeholder="e.g., Bank statement for account ending in 5466"
                        value={newCustomDocumentName}
                        onChange={(e) => setNewCustomDocumentName(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && addCustomDocument()}
                        className="flex-1"
                      />
                      <Button 
                        size="sm" 
                        onClick={addCustomDocument}
                        disabled={!newCustomDocumentName.trim()}
                      >
                        Add
                      </Button>
                      <Button 
                        size="sm" 
                        variant="ghost"
                        onClick={() => {
                          setShowAddCustomDocument(false);
                          setNewCustomDocumentName("");
                        }}
                      >
                        <X className="w-4 h-4" />
                      </Button>
                    </div>
                  )}
                </div>

                {missingDocuments.length === 0 ? (
                  <p className="text-center text-gray-500">
                    All required documents have been completed!
                  </p>
                ) : (
                  <div className="space-y-6">
                    {/* Group missing documents by category */}
                    {[
                      { 
                        key: 'borrower', 
                        title: 'Borrower & Entity Documents',
                        docs: missingDocuments.filter(req => req.category === 'borrower')
                      },
                      { 
                        key: 'financial', 
                        title: 'Financial Documents',
                        docs: missingDocuments.filter(req => 
                          req.name.includes('Bank Statement') || 
                          req.name.includes('Voided Check')
                        )
                      },
                      { 
                        key: 'property', 
                        title: 'Property Ownership',
                        docs: missingDocuments.filter(req => 
                          req.category === 'property' && 
                          !req.name.includes('Current Lender') && 
                          !req.name.includes('Payoff') &&
                          req.name !== 'Appraisal'
                        )
                      },
                      { 
                        key: 'appraisal', 
                        title: 'Appraisal',
                        docs: missingDocuments.filter(req => req.name === 'Appraisal')
                      },
                      { 
                        key: 'insurance', 
                        title: 'Insurance',
                        docs: missingDocuments.filter(req => req.category === 'insurance')
                      },
                      { 
                        key: 'title', 
                        title: 'Title',
                        docs: missingDocuments.filter(req => req.category === 'title')
                      },
                      { 
                        key: 'payoff', 
                        title: 'Payoff Information',
                        docs: missingDocuments.filter(req => 
                          req.name.includes('Current Lender') || 
                          req.name.includes('Payoff')
                        )
                      },
                      { 
                        key: 'lender_specific', 
                        title: 'Lender-Specific Documents',
                        docs: missingDocuments.filter(req => 
                          req.name.includes('Borrowing Authorization') ||
                          req.name.includes('Disclosure Form')
                        )
                      },
                      { 
                        key: 'custom', 
                        title: 'Custom Requirements',
                        docs: missingDocuments.filter(req => req.category === 'custom')
                      }
                    ].map(category => {
                      if (category.docs.length === 0) return null;

                      return (
                        <div key={category.key} className="space-y-3">
                          <h4 className="font-medium text-gray-900 border-b pb-2">
                            {category.title}
                            <span className="ml-2 text-sm text-gray-500">
                              {category.docs.length} missing
                            </span>
                          </h4>

                          <div className="space-y-3">
                            {category.docs.map((req, index) => (
                              <div key={index} className="border rounded-lg p-4">
                                <div className="flex items-center justify-between mb-2">
                                  <div className="flex items-center gap-3">
                                    <File className="w-4 h-4 text-gray-400" />
                                    <span className="font-medium">{req.name}</span>
                                    <Badge variant="outline" className="text-xs">
                                      {req.category}
                                    </Badge>
                                    {(req.name.includes('Borrowing Authorization') || req.name.includes('Disclosure Form')) && (
                                      <Badge className="bg-blue-100 text-blue-800 border-blue-300 text-xs">
                                        Kiavi Specific
                                      </Badge>
                                    )}
                                  </div>
                                  <div className="flex items-center gap-2">
                                    <Button 
                                      size="sm" 
                                      variant="outline"
                                      onClick={() => markRequirementComplete(req.name)}
                                      className="text-green-600 hover:text-green-700"
                                    >
                                      <Check className="w-4 h-4 mr-1" />
                                      Mark Complete
                                    </Button>
                                    {req.category === "custom" && (
                                      <Button 
                                        size="sm" 
                                        variant="ghost"
                                        onClick={() => removeCustomDocument(req.name)}
                                        className="text-red-600 hover:text-red-700"
                                        title="Remove custom document"
                                      >
                                        <X className="w-4 h-4" />
                                      </Button>
                                    )}
                                  </div>
                                </div>

                                <div className="flex items-center gap-2 mt-2">
                                  <Select onValueChange={(value) => assignDocumentToRequirement(req.name, value)}>
                                    <SelectTrigger className="w-[300px]">
                                      <SelectValue placeholder="Assign uploaded document..." />
                                    </SelectTrigger>
                                    <SelectContent>
                                      {documents.filter(doc => {
                                        // Filter out documents that are already assigned to any requirement
                                        const allAssignedDocIds = Object.values(assignedDocuments).flat();
                                        return !allAssignedDocIds.includes(doc.id.toString());
                                      }).map((doc) => (
                                        <SelectItem key={doc.id} value={doc.id.toString()}>
                                          {doc.name}
                                        </SelectItem>
                                      ))}
                                    </SelectContent>
                                  </Select>
                                  <Button 
                                    size="sm" 
                                    variant="ghost"
                                    onClick={() => setShowInlineUpload(showInlineUpload === req.name ? null : req.name)}
                                  >
                                    <Plus className="w-4 h-4 mr-1" />
                                    {showInlineUpload === req.name ? 'Cancel Upload' : 'Upload New'}
                                  </Button>
                                </div>

                                {assignedDocuments[req.name] && assignedDocuments[req.name].length > 0 && (
                                  <div className="mt-2 pt-2 border-t">
                                    <p className="text-sm text-gray-600 mb-1">Assigned documents:</p>
                                    <div className="space-y-1">
                                      {assignedDocuments[req.name].map((docId) => {
                                        const doc = documents.find(d => d.id.toString() === docId);
                                        return doc ? (
                                          <div key={docId} className="flex items-center justify-between p-2 bg-green-50 rounded text-sm">
                                            <div className="flex items-center gap-2">
                                              {getFileIcon(doc)}
                                              <span className="text-green-700">{doc.name}</span>
                                            </div>
                                            <div className="flex items-center gap-1">
                                              <Button
                                                size="sm"
                                                variant="ghost"
                                                onClick={() => handleDocumentPreview(doc)}
                                                className="h-6 px-2 text-blue-600 hover:text-blue-700"
                                              >
                                                <Eye className="w-3 h-3" />
                                              </Button>
                                              <Button
                                                size="sm"
                                                variant="ghost"
                                                onClick={() => removeDocumentFromRequirement(req.name, docId)}
                                                className="h-6 px-2 text-red-600 hover:text-red-700"
                                              >
                                                <X className="w-3 h-3" />
                                              </Button>
                                            </div>
                                          </div>
                                        ) : null;
                                      })}
                                    </div>
                                  </div>
                                )}

                                {showInlineUpload === req.name && (
                                  <div className="mt-3 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                                    <div className="flex items-center justify-between mb-3">
                                      <h5 className="text-sm font-medium text-blue-900">Upload for {req.name}</h5>
                                      <Button size="sm" variant="ghost" onClick={() => setShowInlineUpload(null)}>
                                        <X className="w-4 h-4" />
                                      </Button>
                                    </div>

                                    <div className="space-y-3">
                                      <div>
                                        <input
                                          type="file"
                                          accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
                                          multiple
                                          onChange={async (e) => {
                                            const files = Array.from(e.target.files || []);
                                            if (files.length > 0) {
                                              const uploadPromises = files.map(async (file) => {
                                                const formData = new FormData();
                                                formData.append('file', file);
                                                formData.append('name', `${req.name} - ${file.name.split('.').slice(0, -1).join('.')}`);
                                                formData.append('category', req.category);

                                                return fetch(`/api/loans/${loanId}/documents`, {
                                                  method: 'POST',
                                                  body: formData
                                                });
                                              });

                                              try {
                                                const responses = await Promise.all(uploadPromises);
                                                const successCount = responses.filter(r => r.ok).length;
                                                const failCount = responses.length - successCount;

                                                const uploadedDocumentIds = [];
                                                for (let i = 0; i < responses.length; i++) {
                                                  if (responses[i].ok) {
                                                    const docData = await responses[i].json();
                                                    uploadedDocumentIds.push(docData.id.toString());
                                                  }
                                                }

                                                uploadedDocumentIds.forEach(docId => {
                                                  assignDocumentToRequirement(req.name, docId);
                                                });

                                                queryClient.invalidateQueries({ queryKey: [`/api/loans/${loanId}`] });
                                                setShowInlineUpload(null);

                                                if (failCount === 0) {
                                                  toast({
                                                    title: "Documents uploaded successfully",
                                                    description: `${successCount} document${successCount > 1 ? 's' : ''} uploaded for ${req.name}`,
                                                  });
                                                } else {
                                                  toast({
                                                    title: "Partial upload success",
                                                    description: `${successCount} uploaded, ${failCount} failed`,
                                                    variant: "destructive"
                                                  });
                                                }
                                              } catch (error) {
                                                toast({
                                                  title: "Upload Failed",
                                                  description: "There was an error uploading your documents.",
                                                  variant: "destructive"
                                                });
                                              }
                                            }
                                          }}
                                          className="hidden"
                                          id={`file-upload-${req.name}`}
                                        />
                                        <Button
                                          variant="outline"
                                          onClick={() => document.getElementById(`file-upload-${req.name}`)?.click()}
                                          className="w-full h-12 border-dashed border-2 flex items-center justify-center"
                                        >
                                          <Upload className="w-4 h-4 mr-2" />
                                          Select Files to Upload
                                        </Button>
                                      </div>
                                    </div>
                                  </div>
                                )}
                              </div>
                            ))}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="upload" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Upload Documents</CardTitle>
                <CardDescription>
                  Upload new documents for this loan. Files will be automatically processed and analyzed.
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div>
                    <input
                      type="file"
                      accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
                      multiple
                      onChange={async (e) => {
                        const files = Array.from(e.target.files || []);
                        if (files.length > 0) {
                          const uploadPromises = files.map(async (file) => {
                            const formData = new FormData();
                            formData.append('file', file);
                            formData.append('name', file.name.split('.').slice(0, -1).join('.'));
                            formData.append('category', 'other');

                            return fetch(`/api/loans/${loanId}/documents`, {
                              method: 'POST',
                              body: formData
                            });
                          });

                          try {
                            const responses = await Promise.all(uploadPromises);
                            const successCount = responses.filter(r => r.ok).length;
                            const failCount = responses.length - successCount;

                            queryClient.invalidateQueries({ queryKey: [`/api/loans/${loanId}`] });
                            setActiveTab("document-list");

                            if (failCount === 0) {
                              toast({
                                title: "Documents uploaded successfully",
                                description: `${successCount} document${successCount > 1 ? 's' : ''} uploaded`,
                              });
                            } else {
                              toast({
                                title: "Partial upload success",
                                description: `${successCount} uploaded, ${failCount} failed`,
                                variant: "destructive"
                              });
                            }
                          } catch (error) {
                            toast({
                              title: "Upload Failed",
                              description: "There was an error uploading your documents.",
                              variant: "destructive"
                            });
                          }
                        }
                      }}
                      className="hidden"
                      id="bulk-file-upload"
                    />
                    <Button
                      variant="outline"
                      onClick={() => document.getElementById('bulk-file-upload')?.click()}
                      className="w-full h-24 border-dashed border-2 flex flex-col items-center justify-center"
                    >
                      <Upload className="w-8 h-8 mb-2" />
                      <span className="text-lg">Select Files to Upload</span>
                      <span className="text-sm text-gray-500">PDF, JPG, PNG, DOC, DOCX</span>
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="completed" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Completed Requirements</CardTitle>
                <CardDescription>
                  Document requirements that have been satisfied and marked as complete.
                </CardDescription>
              </CardHeader>
              <CardContent>
                {completedDocuments.length === 0 ? (
                  <p className="text-center text-gray-500">
                    No requirements have been completed yet.
                  </p>
                ) : (
                  <div className="space-y-3">
                    {completedDocuments.map((req, index) => (
                      <div key={index} className="flex items-center justify-between p-3 border rounded-lg bg-green-50">
                        <div className="flex items-center gap-3">
                          <Check className="w-5 h-5 text-green-600" />
                          <div>
                            <span className="font-medium text-green-800">{req.name}</span>
                            <Badge variant="outline" className="ml-2 text-xs">
                              {req.category}
                            </Badge>
                            {assignedDocuments[req.name] && assignedDocuments[req.name].length > 0 && (
                              <div className="mt-2">
                                <p className="text-xs text-green-600 mb-1">Assigned documents:</p>
                                <div className="space-y-1">
                                  {assignedDocuments[req.name].map((docId) => {
                                    const doc = documents.find(d => d.id.toString() === docId);
                                    return doc ? (
                                      <div key={docId} className="flex items-center justify-between p-2 bg-green-100 rounded text-sm">
                                        <div className="flex items-center gap-2">
                                          {getFileIcon(doc)}
                                          <span className="text-green-800">{doc.name}</span>
                                        </div>
                                        <div className="flex items-center gap-1">
                                          <Button
                                            size="sm"
                                            variant="ghost"
                                            onClick={() => handleDocumentPreview(doc)}
                                            className="h-6 px-2 text-blue-600 hover:text-blue-700"
                                          >
                                            <Eye className="w-3 h-3" />
                                          </Button>
                                          <Button
                                            size="sm"
                                            variant="ghost"
                                            onClick={() => removeDocumentFromRequirement(req.name, docId)}
                                            className="h-6 px-2 text-red-600 hover:text-red-700"
                                          >
                                            <X className="w-3 h-3" />
                                          </Button>
                                        </div>
                                      </div>
                                    ) : null;
                                  })}
                                </div>
                              </div>
                            )}
                          </div>
                        </div>
                        <Button 
                          size="sm" 
                          variant="ghost"
                          onClick={() => unmarkRequirementComplete(req.name)}
                          className="text-gray-500 hover:text-gray-700"
                        >
                          Unmark
                        </Button>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>

        {/* Deleted Documents Section - Bottom */}
        {deletedDocuments.length > 0 && (
          <div className="mt-6">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-sm font-medium text-gray-700">Deleted Documents</h3>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowDeleted(!showDeleted)}
                className="text-xs text-gray-500 hover:text-gray-700"
              >
                {showDeleted ? 'Hide' : 'Show'} ({deletedDocuments.length})
              </Button>
            </div>

            {showDeleted && (
              <div className="space-y-2 p-3 bg-gray-50 rounded-lg border">
                {deletedDocuments.map((doc) => (
                  <div key={doc.id} className="flex items-center justify-between p-2 bg-white rounded border">
                    <div className="flex items-center gap-2">
                      {getFileIcon(doc)}
                      <span className="text-sm text-gray-500 line-through">
                        {doc.name}
                      </span>
                      <Badge variant="secondary" className="text-xs">
                        {doc.category || 'other'}
                      </Badge>
                    </div>
                    <div className="flex items-center gap-2">
                      <div className="text-xs text-gray-400">
                        Deleted {doc.uploadedAt && format(new Date(doc.uploadedAt), 'MMM d, yyyy')}
                      </div>
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => restoreDocument(doc.id)}
                        className="h-6 px-2 text-blue-600 hover:text-blue-700"
                        title="Restore document"
                      >
                        <RotateCcw className="w-3 h-3" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>

      {/* Reset Documents Confirmation Dialog */}
      <AlertDialog open={showResetConfirmation} onOpenChange={setShowResetConfirmation}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Reset All Documents</AlertDialogTitle>
            <AlertDialogDescription>
              This action will permanently delete ALL documents from both the active documents section and the deleted documents section. This cannot be undone.
              <br /><br />
              Are you sure you want to reset all documents for this loan?
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction 
              onClick={resetAllDocuments}
              disabled={isResetting}
              className="bg-red-600 hover:bg-red-700"
            >
              {isResetting ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Resetting...
                </>
              ) : (
                'Yes, Reset All Documents'
              )}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

client/src/components/DocumentProgress.t.content:
import { Document, Contact } from "@/lib/types";
import { useState } from "react";
import DocumentChecklist from "./DocumentChecklist";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

interface DocumentProgressProps {
  documents: Document[];
  requiredDocuments: {
    borrower: string[];
    property: string[];
    title: string[];
    insurance: string[];
  };
  contacts?: Contact[];
  loanDetails?: any;
  completedRequirements?: Set<string>;
  onCompletedRequirementsChange?: (completed: Set<string>) => void;
  documentAssignments?: Record<string, string[]>;
}

export default function DocumentProgress({ documents, requiredDocuments, contacts = [], loanDetails, completedRequirements = new Set(), onCompletedRequirementsChange, documentAssignments = {} }: DocumentProgressProps) {
  const [showChecklist, setShowChecklist] = useState(false);
  const [showAssignments, setShowAssignments] = useState(false);

  // Get all assigned document IDs
  const assignedDocumentIds = new Set(
    Object.values(documentAssignments).flat()
  );

  // Get unassigned documents (not assigned to any requirement, regardless of category)
  const unassignedDocs = documents.filter(doc => 
    !assignedDocumentIds.has(doc.id.toString())
  );

  // Get all required document names for assignment dropdown
  const allRequiredDocs = [
    ...requiredDocuments.borrower.map(name => ({ name, category: 'borrower' })),
    ...requiredDocuments.title.map(name => ({ name, category: 'title' })),
    ...requiredDocuments.insurance.map(name => ({ name, category: 'insurance' }))
  ];

  // Helper function to find contact by role
  const findContactByRole = (role: string) => {
    return contacts.find(contact => 
      contact.role.toLowerCase().includes(role.toLowerCase())
    );
  };

  // Find specific contacts
  const titleContact = findContactByRole("title");
  const insuranceContact = findContactByRole("insurance");

  // Calculate completed requirements for each category based on manual assignments
  const borrowerDocs = requiredDocuments.borrower.filter(req => completedRequirements.has(req)).length;
  const titleDocs = requiredDocuments.title.filter(req => completedRequirements.has(req)).length;
  const insuranceDocs = requiredDocuments.insurance.filter(req => completedRequirements.has(req)).length;

  // Calculate required counts
  const borrowerRequired = requiredDocuments.borrower.length;
  const titleRequired = requiredDocuments.title.length;
  const insuranceRequired = requiredDocuments.insurance.length;

  // Calculate percentages
  const borrowerPercentage = Math.round((borrowerDocs / borrowerRequired) * 100) || 0;
  const titlePercentage = Math.round((titleDocs / titleRequired) * 100) || 0;
  const insurancePercentage = Math.round((insuranceDocs / insuranceRequired) * 100) || 0;

  // Calculate overall percentage
  const totalDocs = borrowerDocs + titleDocs + insuranceDocs;
  const totalRequired = borrowerRequired + titleRequired + insuranceRequired;
  const overallPercentage = Math.round((totalDocs / totalRequired) * 100) || 0;

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-100" data-component="document-progress">
      <div className="px-4 py-4 sm:px-6 border-b border-gray-100 flex justify-between items-center">
        <div>
          <h3 className="text-lg leading-6 font-heading font-medium text-gray-900 flex items-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
            Document Status
          </h3>
          <p className="mt-1 max-w-2xl text-sm text-gray-500">
            Current progress of required documents
          </p>
        </div>
        <div className="flex gap-2">
          {unassignedDocs.length > 0 && (
            <button 
              onClick={() => setShowAssignments(true)}
              className="text-sm font-medium text-amber-600 hover:text-amber-700 flex items-center bg-amber-50 px-3 py-1 rounded-md border border-amber-200"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 4V2a1 1 0 011-1h4a1 1 0 011 1v2M7 4h6M7 4l-2 14h8l-2-14m-5 4v6m4-6v6" />
              </svg>
              Assign Files ({unassignedDocs.length})
            </button>
          )}
          <button 
            onClick={() => setShowChecklist(true)}
            className="text-sm font-medium text-blue-600 hover:text-blue-700 flex items-center"
          >
            View full checklist
            <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 ml-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>
      </div>

      <div className="px-4 py-4 sm:px-6">
        {/* Overall progress bar */}
        <div className="mb-5">
          <div className="flex justify-between items-center mb-1">
            <span className="text-sm font-medium text-gray-700 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
              </svg>
              Overall Completion
            </span>
            <span className="text-sm font-bold bg-blue-100 text-blue-800 px-2 py-0.5 rounded-full">
              {overallPercentage}%
            </span>
          </div>
          <div className="w-full bg-gray-100 rounded-full h-3 mt-2">
            <div 
              className={`h-3 rounded-full ${
                overallPercentage < 30 ? 'bg-red-500' : 
                overallPercentage < 70 ? 'bg-yellow-500' : 'bg-green-500'
              }`}
              style={{ width: `${overallPercentage}%` }}
            ></div>
          </div>
        </div>

        {/* Main document categories */}
        <div className="space-y-5 mt-6">
          {/* Borrower Documents */}
          <div className="bg-blue-50 rounded-lg p-3">
            <div className="flex justify-between items-center mb-2">
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-2 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
                <span className="text-sm font-medium text-gray-800">Borrower Documents</span>
              </div>
              <div className="flex items-center">
                <span className={`inline-flex items-center px-2 py-0.5 rounded text-xs font-medium ${
                  borrowerPercentage === 100 
                    ? 'bg-green-100 text-green-800' 
                    : borrowerPercentage > 0 
                      ? 'bg-yellow-100 text-yellow-800' 
                      : 'bg-red-100 text-red-800'
                }`}>
                  {borrowerDocs}/{borrowerRequired}
                </span>
              </div>
            </div>
            <div className="w-full bg-white rounded-full h-2">
              <div 
                className={`h-2 rounded-full ${
                  borrowerPercentage === 100 
                    ? 'bg-green-500' 
                    : borrowerPercentage > 0 
                      ? 'bg-yellow-500' 
                      : 'bg-red-500'
                }`}
                style={{ width: `${borrowerPercentage}%` }}
              ></div>
            </div>
            <div className="mt-1 text-xs text-blue-800">
              John Smith ‚Ä¢ Copy: (555) 123-4567 ‚Ä¢ john.smith@example.com
            </div>
          </div>

          {/* Title Documents */}
          <div className="bg-purple-50 rounded-lg p-3">
            <div className="flex justify-between items-center mb-2">
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-2 text-purple-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
                </svg>
                <span className="text-sm font-medium text-gray-800">Title Documents</span>
              </div>
              <div className="flex items-center">
                <span className={`inline-flex items-center px-2 py-0.5 rounded text-xs font-medium ${
                  titlePercentage === 100 
                    ? 'bg-green-100 text-green-800' 
                    : titlePercentage > 0 
                      ? 'bg-yellow-100 text-yellow-800' 
                      : 'bg-red-100 text-red-800'
                }`}>
                  {titleDocs}/{titleRequired}
                </span>
              </div>
            </div>
            <div className="w-full bg-white rounded-full h-2">
              <div 
                className={`h-2 rounded-full ${
                  titlePercentage === 100 
                    ? 'bg-green-500' 
                    : titlePercentage > 0 
                      ? 'bg-yellow-500' 
                      : 'bg-red-500'
                }`}
                style={{ width: `${titlePercentage}%` }}
              ></div>
            </div>
            {titleContact && (
              <div className="mt-1 text-xs text-purple-800">
                {titleContact.company ? titleContact.company : titleContact.name} ‚Ä¢ {titleContact.phone} ‚Ä¢ {titleContact.email}
              </div>
            )}
          </div>

          {/* Insurance Documents */}
          <div className="bg-green-50 rounded-lg p-3">
            <div className="flex justify-between items-center mb-2">
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-2 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
                </svg>
                <span className="text-sm font-medium text-gray-800">Insurance Documents</span>
              </div>
              <div className="flex items-center">
                <span className={`inline-flex items-center px-2 py-0.5 rounded text-xs font-medium ${
                  insurancePercentage === 100 
                    ? 'bg-green-100 text-green-800' 
                    : insurancePercentage > 0 
                      ? 'bg-yellow-100 text-yellow-800' 
                      : 'bg-red-100 text-red-800'
                }`}>
                  {insuranceDocs}/{insuranceRequired}
                </span>
              </div>
            </div>
            <div className="w-full bg-white rounded-full h-2">
              <div 
                className={`h-2 rounded-full ${
                  insurancePercentage === 100 
                    ? 'bg-green-500' 
                    : insurancePercentage > 0 
                      ? 'bg-yellow-500' 
                      : 'bg-red-500'
                }`}
                style={{ width: `${insurancePercentage}%` }}
              ></div>
            </div>
            {insuranceContact && (
              <div className="mt-1 text-xs text-green-800">
                {insuranceContact.company ? insuranceContact.company : insuranceContact.name} ‚Ä¢ {insuranceContact.phone} ‚Ä¢ {insuranceContact.email}
              </div>
            )}
          </div>
        </div>


      </div>

      {/* Document Checklist Modal */}
      <Dialog open={showChecklist} onOpenChange={setShowChecklist}>
        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Complete Document Checklist</DialogTitle>
          </DialogHeader>
          <DocumentChecklist 
            loanDetails={loanDetails || { lender: { name: "AHL" } }}
          />
        </DialogContent>
      </Dialog>

      {/* File Assignment Modal */}
      <Dialog open={showAssignments} onOpenChange={setShowAssignments}>
        <DialogContent className="max-w-3xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Assign Uploaded Files to Document Requirements</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <p className="text-sm text-gray-600">
              Match your uploaded Google Drive files to the specific document requirements below.
            </p>

            {unassignedDocs.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-12 w-12 mx-auto mb-4 text-gray-300" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
                <p>All files have been assigned to document requirements.</p>
              </div>
            ) : (
              <div className="space-y-4">
                {unassignedDocs.map((doc, index) => (
                  <div key={index} className="border border-gray-200 rounded-lg p-4 bg-gray-50">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <h4 className="text-sm font-medium text-gray-900 mb-1">{doc.name}</h4>
                        <p className="text-xs text-gray-500 mb-3">
                          Uploaded: {doc.uploadedAt ? new Date(doc.uploadedAt).toLocaleDateString() : 'Date unknown'}
                        </p>

                        <div className="flex items-center gap-2">
                          <label className="text-xs font-medium text-gray-700">
                            Assign to requirement:
                          </label>
                          <Select onValueChange={(value) => {
                            const [category, docName] = value.split('|');
                            // Handle assignment logic here
                            console.log(`Assigning ${doc.name} to ${docName} in ${category} category`);
                          }}>
                            <SelectTrigger className="w-64">
                              <SelectValue placeholder="Select document type..." />
                            </SelectTrigger>
                            <SelectContent>
                              <div className="p-2 text-xs font-medium text-blue-700 bg-blue-50">Borrower Documents</div>
                              {requiredDocuments.borrower.map((reqDoc, idx) => (
                                <SelectItem key={`borrower-${idx}`} value={`borrower|${reqDoc}`}>
                                  {reqDoc}
                                </SelectItem>
                              ))}
                              <div className="p-2 text-xs font-medium text-purple-700 bg-purple-50 mt-2">Title Documents</div>
                              {requiredDocuments.title.map((reqDoc, idx) => (
                                <SelectItem key={`title-${idx}`} value={`title|${reqDoc}`}>
                                  {reqDoc}
                                </SelectItem>
                              ))}
                              <div className="p-2 text-xs font-medium text-green-700 bg-green-50 mt-2">Insurance Documents</div>
                              {requiredDocuments.insurance.map((reqDoc, idx) => (
                                <SelectItem key={`insurance-${idx}`} value={`insurance|${reqDoc}`}>
                                  {reqDoc}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        </div>
                      </div>

                      <div className="ml-4">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                        </svg>
                      </div>
                    </div>
                  </div>
                ))}

                <div className="flex justify-end pt-4 border-t border-gray-200">
                  <Button variant="outline" onClick={() => setShowAssignments(false)}>
                    Done Assigning
                  </Button>
                </div>
              </div>
            )}
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/EditableLoanDetail.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Edit, Save, X } from "lucide-react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface EditableLoanDetailsProps {
  loanId: number;
  loanDetails: any;
}

export default function EditableLoanDetails({ loanId, loanDetails }: EditableLoanDetailsProps) {
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({
    loanNumber: loanDetails?.loan?.loanNumber || "",
    borrowerName: loanDetails?.loan?.borrowerName || "",
    borrowerEntityName: loanDetails?.loan?.borrowerEntityName || "",
    propertyAddress: loanDetails?.loan?.propertyAddress || "",
    propertyType: loanDetails?.loan?.propertyType || "",
    loanAmount: loanDetails?.loan?.loanAmount || "",
    loanType: loanDetails?.loan?.loanType || "",
    loanPurpose: loanDetails?.loan?.loanPurpose || "",
    funder: loanDetails?.loan?.funder || "",
    status: loanDetails?.loan?.status || "",
    targetCloseDate: loanDetails?.loan?.targetCloseDate || "",
    notes: loanDetails?.loan?.notes || ""
  });

  const { toast } = useToast();

  const updateLoanMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await fetch(`/api/loans/${loanId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error('Failed to update loan');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/loans', loanId] });
      queryClient.invalidateQueries({ queryKey: ['/api/loans'] });
      setIsEditing(false);
      toast({
        title: "Success",
        description: "Loan details updated successfully"
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update loan details",
        variant: "destructive"
      });
    }
  });

  const handleSave = () => {
    updateLoanMutation.mutate(editData);
  };

  const handleCancel = () => {
    setEditData({
      loanNumber: loanDetails?.loan?.loanNumber || "",
      borrowerName: loanDetails?.loan?.borrowerName || "",
      borrowerEntityName: loanDetails?.loan?.borrowerEntityName || "",
      propertyAddress: loanDetails?.loan?.propertyAddress || "",
      propertyType: loanDetails?.loan?.propertyType || "",
      loanAmount: loanDetails?.loan?.loanAmount || "",
      loanType: loanDetails?.loan?.loanType || "",
      loanPurpose: loanDetails?.loan?.loanPurpose || "",
      funder: loanDetails?.loan?.funder || "",
      status: loanDetails?.loan?.status || "",
      targetCloseDate: loanDetails?.loan?.targetCloseDate || "",
      notes: loanDetails?.loan?.notes || ""
    });
    setIsEditing(false);
  };

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="text-lg">Loan Details</CardTitle>
        {!isEditing ? (
          <Button
            variant="outline"
            size="sm"
            onClick={() => setIsEditing(true)}
          >
            <Edit className="w-4 h-4 mr-1" />
            Edit
          </Button>
        ) : (
          <div className="flex gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleCancel}
            >
              <X className="w-4 h-4 mr-1" />
              Cancel
            </Button>
            <Button
              size="sm"
              onClick={handleSave}
              disabled={updateLoanMutation.isPending}
            >
              <Save className="w-4 h-4 mr-1" />
              {updateLoanMutation.isPending ? 'Saving...' : 'Save'}
            </Button>
          </div>
        )}
      </CardHeader>

      <CardContent className="space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {/* Loan Information */}
          <div className="space-y-3">
            <h3 className="font-medium text-sm text-muted-foreground">Loan Information</h3>

            <div>
              <Label htmlFor="loanNumber">Loan Number</Label>
              {isEditing ? (
                <Input
                  id="loanNumber"
                  value={editData.loanNumber}
                  onChange={(e) => setEditData({...editData, loanNumber: e.target.value})}
                  className="mt-1"
                  placeholder="LN-0001"
                />
              ) : (
                <p className="mt-1 text-sm font-medium">{loanDetails?.loan?.loanNumber || "Not assigned"}</p>
              )}
            </div>

            <div>
              <Label htmlFor="borrowerName">Borrower Name</Label>
              {isEditing ? (
                <Input
                  id="borrowerName"
                  value={editData.borrowerName}
                  onChange={(e) => setEditData({...editData, borrowerName: e.target.value})}
                  className="mt-1"
                />
              ) : (
                <p className="mt-1 text-sm">{loanDetails?.loan?.borrowerName || "Not specified"}</p>
              )}
            </div>

            <div>
              <Label htmlFor="borrowerEntityName">Entity Name</Label>
              {isEditing ? (
                <Input
                  id="borrowerEntityName"
                  value={editData.borrowerEntityName}
                  onChange={(e) => setEditData({...editData, borrowerEntityName: e.target.value})}
                  className="mt-1"
                />
              ) : (
                <p className="mt-1 text-sm">{loanDetails?.loan?.borrowerEntityName || "Not specified"}</p>
              )}
            </div>
          </div>

          {/* Property Information */}
          <div className="space-y-3">
            <h3 className="font-medium text-sm text-muted-foreground">Property Information</h3>

            <div>
