client/src/components/LoanSetupWizard.ts.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Progress } from "@/components/ui/progress";
import { CheckCircle, Circle, ArrowRight, ArrowLeft } from "lucide-react";

interface LoanSetupWizardProps {
  isOpen: boolean;
  onClose: () => void;
  loanId?: number;
}

interface Step {
  id: string;
  title: string;
  description: string;
  instructions: string[];
  completed: boolean;
  optional?: boolean;
}

export default function LoanSetupWizard({ isOpen, onClose, loanId }: LoanSetupWizardProps) {
  const [currentStep, setCurrentStep] = useState(0);

  const steps: Step[] = [
    {
      id: "loan-details",
      title: "Enter Loan Details",
      description: "Set up the basic loan information",
      instructions: [
        "Fill in the borrower name and contact information",
        "Enter the property address and details",
        "Select the loan type (Purchase, Refinance, etc.)",
        "Set the loan amount and target close date",
        "Choose the lender from the dropdown"
      ],
      completed: false
    },
    {
      id: "google-drive",
      title: "Connect Google Drive",
      description: "Link the loan documents folder",
      instructions: [
        "Click 'Connect Google Drive' in the Documents section",
        "Authorize access to your Google Drive account",
        "Either select an existing folder or create a new one",
        "Name the folder: '[Borrower Name] - [Property Address]'",
        "Upload or organize loan documents in the folder"
      ],
      completed: false
    },
    {
      id: "add-contacts",
      title: "Add Key Contacts",
      description: "Set up all people involved in the loan",
      instructions: [
        "Add the borrower with their email and phone number",
        "Add title company contact (for title services)",
        "Add insurance agent contact (for property insurance)",
        "Add current lender contact (for payoff requests)",
        "Add any loan analysts from your team",
        "Verify all email addresses are correct"
      ],
      completed: false
    },
    {
      id: "send-emails",
      title: "Send Initial Emails",
      description: "Notify all parties about the new loan file",
      instructions: [
        "Review the contact list for accuracy",
        "Click 'Email' button next to each contact",
        "Review and customize each email template",
        "Send title request to title company",
        "Send insurance request to insurance agent",
        "Send payoff request to current lender",
        "Send document request to borrower"
      ],
      completed: false
    },
    {
      id: "setup-tasks",
      title: "Create Action Items",
      description: "Set up follow-up tasks and reminders",
      instructions: [
        "Review the automatically generated missing documents list",
        "Create tasks for document follow-ups",
        "Set due dates for time-sensitive items",
        "Add any custom tasks specific to this loan",
        "Assign priority levels to urgent items"
      ],
      completed: false,
      optional: true
    },
    {
      id: "review",
      title: "Final Review",
      description: "Confirm everything is set up correctly",
      instructions: [
        "Review loan details for accuracy",
        "Confirm all contacts have been notified",
        "Check that Google Drive folder is properly connected",
        "Verify initial emails have been sent",
        "Review the dashboard for any missing items",
        "The loan is now ready for processing!"
      ],
      completed: false
    }
  ];

  const progress = (currentStep / (steps.length - 1)) * 100;

  const nextStep = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const markStepComplete = () => {
    steps[currentStep].completed = true;
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[700px] max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4M7.835 4.697a3.42 3.42 0 001.946-.806 3.42 3.42 0 014.438 0 3.42 3.42 0 001.946.806 3.42 3.42 0 013.138 3.138 3.42 3.42 0 00.806 1.946 3.42 3.42 0 010 4.438 3.42 3.42 0 00-.806 1.946 3.42 3.42 0 01-3.138 3.138 3.42 3.42 0 00-1.946.806 3.42 3.42 0 01-4.438 0 3.42 3.42 0 00-1.946-.806 3.42 3.42 0 01-3.138-3.138 3.42 3.42 0 00-.806-1.946 3.42 3.42 0 010-4.438 3.42 3.42 0 00.806-1.946 3.42 3.42 0 013.138-3.138z" />
            </svg>
            Loan Setup Wizard
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Progress Bar */}
          <div className="space-y-2">
            <div className="flex justify-between text-sm text-gray-600">
              <span>Step {currentStep + 1} of {steps.length}</span>
              <span>{Math.round(progress)}% Complete</span>
            </div>
            <Progress value={progress} className="w-full" />
          </div>

          {/* Step Navigation */}
          <div className="flex space-x-2 overflow-x-auto pb-2">
            {steps.map((step, index) => (
              <button
                key={step.id}
                onClick={() => setCurrentStep(index)}
                className={`flex items-center space-x-2 px-3 py-2 rounded-lg text-sm whitespace-nowrap transition-colors ${
                  currentStep === index
                    ? 'bg-blue-100 text-blue-700 border border-blue-200'
                    : step.completed
                    ? 'bg-green-100 text-green-700'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                {step.completed ? (
                  <CheckCircle className="h-4 w-4" />
                ) : (
                  <Circle className="h-4 w-4" />
                )}
                <span>{step.title}</span>
                {step.optional && (
                  <span className="text-xs bg-blue-200 text-blue-700 px-1 rounded">Optional</span>
                )}
              </button>
            ))}
          </div>

          {/* Current Step Content */}
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
            <div className="flex items-start space-x-3">
              <div className="mt-1">
                {steps[currentStep].completed ? (
                  <CheckCircle className="h-6 w-6 text-green-600" />
                ) : (
                  <Circle className="h-6 w-6 text-blue-600" />
                )}
              </div>
              <div className="flex-1">
                <h3 className="text-lg font-semibold text-gray-900 mb-2">
                  {steps[currentStep].title}
                  {steps[currentStep].optional && (
                    <span className="ml-2 text-sm font-normal text-blue-600">(Optional)</span>
                  )}
                </h3>
                <p className="text-gray-700 mb-4">{steps[currentStep].description}</p>

                <div className="space-y-2">
                  <h4 className="font-medium text-gray-900">Instructions:</h4>
                  <ul className="space-y-1">
                    {steps[currentStep].instructions.map((instruction, index) => (
                      <li key={index} className="flex items-start space-x-2 text-sm text-gray-700">
                        <span className="bg-blue-200 text-blue-700 rounded-full w-5 h-5 flex items-center justify-center text-xs font-medium mt-0.5">
                          {index + 1}
                        </span>
                        <span>{instruction}</span>
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Step-specific action buttons */}
                <div className="mt-4 flex flex-wrap gap-2">
                  {currentStep === 0 && (
                    <Button size="sm" onClick={() => window.location.href = `#/loans/${loanId || 'new'}`}>
                      Go to Loan Details
                    </Button>
                  )}
                  {currentStep === 1 && (
                    <Button size="sm" onClick={() => window.location.href = '#/documents'}>
                      Connect Google Drive
                    </Button>
                  )}
                  {currentStep === 2 && (
                    <Button size="sm" onClick={() => window.location.href = `#/loans/${loanId}/contacts`}>
                      Add Contacts
                    </Button>
                  )}
                  {currentStep === 3 && (
                    <Button size="sm" onClick={() => window.location.href = `#/loans/${loanId}/contacts`}>
                      Send Emails
                    </Button>
                  )}
                  {currentStep === 4 && (
                    <Button size="sm" onClick={() => window.location.href = `#/loans/${loanId}/tasks`}>
                      Create Tasks
                    </Button>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Navigation Buttons */}
          <div className="flex justify-between items-center">
            <Button
              variant="outline"
              onClick={prevStep}
              disabled={currentStep === 0}
              className="flex items-center space-x-2"
            >
              <ArrowLeft className="h-4 w-4" />
              <span>Previous</span>
            </Button>

            <div className="flex space-x-2">
              {!steps[currentStep].completed && (
                <Button
                  variant="outline"
                  onClick={markStepComplete}
                  className="text-green-600 border-green-300 hover:bg-green-50"
                >
                  Mark Complete
                </Button>
              )}

              {currentStep === steps.length - 1 ? (
                <Button onClick={onClose} className="flex items-center space-x-2">
                  <CheckCircle className="h-4 w-4" />
                  <span>Finish Setup</span>
                </Button>
              ) : (
                <Button onClick={nextStep} className="flex items-center space-x-2">
                  <span>Next</span>
                  <ArrowRight className="h-4 w-4" />
                </Button>
              )}
            </div>
          </div>

          {/* Tips Section */}
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <h4 className="font-medium text-yellow-800 mb-2">üí° Pro Tips:</h4>
            <ul className="text-sm text-yellow-700 space-y-1">
              <li>‚Ä¢ You can come back to this wizard anytime from the loan dashboard</li>
              <li>‚Ä¢ Complete steps in order for the best workflow experience</li>
              <li>‚Ä¢ Optional steps can be skipped if not needed for your loan type</li>
              <li>‚Ä¢ All contact emails will use your loan-specific templates automatically</li>
            </ul>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/LoanWizard.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Progress } from "@/components/ui/progress";
import { ChevronLeft, ChevronRight, Home, Users, Building, Phone, Upload, CheckCircle, FileText } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useQueryClient } from "@tanstack/react-query";

// Step schemas
const propertySchema = z.object({
  address: z.string().min(5, "Property address is required"),
  propertyType: z.string().min(1, "Property type is required"),
  estimatedValue: z.string().optional(),
});

const borrowerSchema = z.object({
  borrowerName: z.string().min(2, "Borrower name is required"),
  borrowerEntityName: z.string().optional(),
  loanAmount: z.string().min(1, "Loan amount is required"),
  loanToValue: z.string().optional(),
  loanType: z.string().min(1, "Loan type is required"),
  loanPurpose: z.string().min(1, "Loan purpose is required"),
  targetCloseDate: z.string().optional(),
});

const funderSchema = z.object({
  funder: z.string().min(1, "Funder is required"),
});

const contactsSchema = z.object({
  borrowerPhone: z.string().optional(),
  borrowerEmail: z.string().optional(),
  titleAgentName: z.string().optional(),
  titleAgentPhone: z.string().optional(),
  titleAgentEmail: z.string().optional(),
  insuranceAgentName: z.string().optional(),
  insuranceAgentPhone: z.string().optional(),
  insuranceAgentEmail: z.string().optional(),
  currentLenderName: z.string().optional(),
  currentLenderPhone: z.string().optional(),
  currentLenderEmail: z.string().optional(),
});

type PropertyData = z.infer<typeof propertySchema>;
type BorrowerData = z.infer<typeof borrowerSchema>;
type FunderData = z.infer<typeof funderSchema>;
type ContactsData = z.infer<typeof contactsSchema>;

interface LoanWizardProps {
  onComplete?: (loanId: number) => void;
  onCancel?: () => void;
}

const steps = [
  { id: 1, title: "Property Details", icon: Home },
  { id: 2, title: "Borrower Info", icon: Users },
  { id: 3, title: "Select Funder", icon: Building },
  { id: 4, title: "Contact Information", icon: Phone },
  { id: 5, title: "Document Upload", icon: Upload },
];

export default function LoanWizard({ onComplete, onCancel }: LoanWizardProps) {
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [propertyData, setPropertyData] = useState<PropertyData | null>(null);
  const [borrowerData, setBorrowerData] = useState<BorrowerData | null>(null);
  const [funderData, setFunderData] = useState<FunderData | null>(null);
  const [contactsData, setContactsData] = useState<ContactsData | null>(null);
  const [createdLoanId, setCreatedLoanId] = useState<number | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const propertyForm = useForm<PropertyData>({
    resolver: zodResolver(propertySchema),
    defaultValues: { address: "", propertyType: "", estimatedValue: "" },
  });

  const borrowerForm = useForm<BorrowerData>({
    resolver: zodResolver(borrowerSchema),
    defaultValues: {
      borrowerName: "",
      borrowerEntityName: "",
      loanAmount: "",
      loanToValue: "",
      loanType: "DSCR",
      loanPurpose: "",
      targetCloseDate: "",
    },
  });

  const funderForm = useForm<FunderData>({
    resolver: zodResolver(funderSchema),
    defaultValues: { funder: "" },
  });

  const contactsForm = useForm<ContactsData>({
    resolver: zodResolver(contactsSchema),
    defaultValues: {
      borrowerPhone: "",
      borrowerEmail: "",
      titleAgentName: "",
      titleAgentPhone: "",
      titleAgentEmail: "",
      insuranceAgentName: "",
      insuranceAgentPhone: "",
      insuranceAgentEmail: "",
      currentLenderName: "",
      currentLenderPhone: "",
      currentLenderEmail: "",
    },
  });

  const progress = (currentStep / steps.length) * 100;

  const handlePropertyNext = async (data: PropertyData) => {
    setPropertyData(data);
    setCurrentStep(2);
  };

  const handleBorrowerNext = async (data: BorrowerData) => {
    setBorrowerData(data);
    setCurrentStep(3);
  };

  const handleFunderNext = async (data: FunderData) => {
    setFunderData(data);
    setCurrentStep(4);
  };

  const handleContactsNext = async (data: ContactsData) => {
    setContactsData(data);

    // Create the loan with all collected data
    if (propertyData && borrowerData && funderData) {
      setLoading(true);
      try {
        // Calculate LTV if both values provided
        let calculatedLTV = null;
        if (borrowerData.loanAmount && propertyData.estimatedValue) {
          const loanAmt = parseInt(borrowerData.loanAmount.replace(/[,$]/g, ''));
          const propValue = parseInt(propertyData.estimatedValue.replace(/[,$]/g, ''));
          if (propValue > 0) {
            calculatedLTV = Math.round((loanAmt / propValue) * 100);
          }
        }

        const finalLTV = borrowerData.loanToValue ? parseInt(borrowerData.loanToValue) : calculatedLTV;

        const loanData = {
          borrowerName: borrowerData.borrowerName,
          borrowerEntityName: borrowerData.borrowerEntityName || borrowerData.borrowerName,
          propertyAddress: propertyData.address,
          propertyType: propertyData.propertyType,
          estimatedValue: propertyData.estimatedValue ? parseInt(propertyData.estimatedValue.replace(/[,$]/g, '')) : null,
          loanAmount: borrowerData.loanAmount,
          loanToValue: finalLTV,
          loanType: borrowerData.loanType,
          loanPurpose: borrowerData.loanPurpose,
          funder: funderData.funder,
          targetCloseDate: borrowerData.targetCloseDate,
          contacts: data,
        };

        const response = await apiRequest("/api/loans", {
          method: "POST",
          body: loanData
        });

        if (response.success) {
          setCreatedLoanId(response.loanId);
          await queryClient.invalidateQueries({ queryKey: ["/api/loans"] });
          setCurrentStep(5);
          toast({
            title: "Success!",
            description: "Loan file created successfully. Now you can upload documents.",
          });
        }
      } catch (error: any) {
        toast({
          title: "Error",
          description: error.message || "Failed to create loan",
          variant: "destructive",
        });
      } finally {
        setLoading(false);
      }
    }
  };

  const handlePrevious = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Home className="w-5 h-5" />
                Property Details
              </CardTitle>
              <CardDescription>
                Let's start by gathering information about the property.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...propertyForm}>
                <form onSubmit={propertyForm.handleSubmit(handlePropertyNext)} className="space-y-4">
                  <FormField
                    control={propertyForm.control}
                    name="address"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Property Address</FormLabel>
                        <FormControl>
                          <Input placeholder="123 Main St, City, State 12345" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={propertyForm.control}
                      name="propertyType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Property Type</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select property type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="single_family">Single Family</SelectItem>
                              <SelectItem value="duplex">Duplex</SelectItem>
                              <SelectItem value="triplex">Triplex</SelectItem>
                              <SelectItem value="quadplex">Quadplex</SelectItem>
                              <SelectItem value="condo">Condo</SelectItem>
                              <SelectItem value="multi_family_5plus">Multi-Family (5+ Units)</SelectItem>
                              <SelectItem value="commercial">Commercial</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={propertyForm.control}
                      name="estimatedValue"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Estimated Value (Optional)</FormLabel>
                          <FormControl>
                            <Input placeholder="500000" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="flex justify-end">
                    <Button type="submit">
                      Next <ChevronRight className="w-4 h-4 ml-2" />
                    </Button>
                  </div>
                </form>
              </Form>
            </CardContent>
          </Card>
        );

      case 2:
        return (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Users className="w-5 h-5" />
                Borrower Information
              </CardTitle>
              <CardDescription>
                Now let's gather the borrower and loan details.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...borrowerForm}>
                <form onSubmit={borrowerForm.handleSubmit(handleBorrowerNext)} className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={borrowerForm.control}
                      name="borrowerName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Borrower Name</FormLabel>
                          <FormControl>
                            <Input placeholder="John Doe" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={borrowerForm.control}
                      name="borrowerEntityName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Entity Name (Optional)</FormLabel>
                          <FormControl>
                            <Input placeholder="ABC Properties LLC" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={borrowerForm.control}
                      name="loanAmount"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Loan Amount</FormLabel>
                          <FormControl>
                            <Input placeholder="400000" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={borrowerForm.control}
                      name="loanToValue"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>LTV % (Optional)</FormLabel>
                          <FormControl>
                            <Input placeholder="80" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={borrowerForm.control}
                      name="loanType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Loan Type</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select loan type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="DSCR">DSCR</SelectItem>
