        );

      case 4:
        return (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Phone className="w-5 h-5" />
                Contact Information
              </CardTitle>
              <CardDescription>
                Gather contact details for all parties involved in the loan.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...contactsForm}>
                <form onSubmit={contactsForm.handleSubmit(handleContactsNext)} className="space-y-6">

                  <div className="space-y-4">
                    <h4 className="font-medium">Borrower Contact</h4>
                    <div className="grid grid-cols-2 gap-4">
                      <FormField
                        control={contactsForm.control}
                        name="borrowerPhone"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Phone</FormLabel>
                            <FormControl>
                              <Input placeholder="(555) 123-4567" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={contactsForm.control}
                        name="borrowerEmail"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input placeholder="borrower@email.com" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                  </div>

                  <div className="space-y-4">
                    <h4 className="font-medium">Title Agent</h4>
                    <div className="grid grid-cols-3 gap-4">
                      <FormField
                        control={contactsForm.control}
                        name="titleAgentName"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Name</FormLabel>
                            <FormControl>
                              <Input placeholder="Agent Name" {...field} />
                            </FormControl>
                            <FormMessage />
