                      <FormField
                        control={contactsForm.control}
                        name="titleAgentPhone"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Phone</FormLabel>
                            <FormControl>
                              <Input placeholder="(555) 123-4567" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={contactsForm.control}
                        name="titleAgentEmail"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input placeholder="title@company.com" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                  </div>

                  <div className="space-y-4">
                    <h4 className="font-medium">Insurance Agent</h4>
                    <div className="grid grid-cols-3 gap-4">
                      <FormField
                        control={contactsForm.control}
                        name="insuranceAgentName"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Name</FormLabel>
                            <FormControl>
                              <Input placeholder="Agent Name" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={contactsForm.control}
                        name="insuranceAgentPhone"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Phone</FormLabel>
                            <FormControl>
                              <Input placeholder="(555) 123-4567" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={contactsForm.control}
                        name="insuranceAgentEmail"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input placeholder="insurance@company.com" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                  </div>

                  <div className="space-y-4">
                    <h4 className="font-medium">Current Lender (if refinance)</h4>
                    <div className="grid grid-cols-3 gap-4">
                      <FormField
                        control={contactsForm.control}
                        name="currentLenderName"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Lender Name</FormLabel>
                            <FormControl>
                              <Input placeholder="Current Lender" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={contactsForm.control}
                        name="currentLenderPhone"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Phone</FormLabel>
                            <FormControl>
                              <Input placeholder="(555) 123-4567" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={contactsForm.control}
                        name="currentLenderEmail"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input placeholder="lender@company.com" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                  </div>

                  <div className="flex justify-between">
                    <Button type="button" variant="outline" onClick={handlePrevious}>
                      <ChevronLeft className="w-4 h-4 mr-2" /> Previous
                    </Button>
                    <Button type="submit" disabled={loading}>
                      {loading ? "Creating Loan..." : "Create Loan & Continue"}
                      <ChevronRight className="w-4 h-4 ml-2" />
                    </Button>
                  </div>
                </form>
              </Form>
            </CardContent>
          </Card>
        );

      case 5:
        return (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CheckCircle className="w-5 h-5 text-green-500" />
                Loan Created Successfully!
              </CardTitle>
              <CardDescription>
                Your loan file has been created. You can now start uploading documents and use the document checklist.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="p-4 bg-green-50 rounded-lg border border-green-200">
                  <div className="flex items-center gap-2 text-green-800">
                    <CheckCircle className="w-4 h-4" />
                    <span className="font-medium">Loan ID: {createdLoanId}</span>
                  </div>
                  <p className="text-sm text-green-700 mt-1">
                    Property: {propertyData?.address}
                  </p>
                  <p className="text-sm text-green-700">
                    Borrower: {borrowerData?.borrowerName}
                  </p>
                  <p className="text-sm text-green-700">
                    Funder: {funderData?.funder}
                  </p>
                </div>

                <div className="flex gap-3">
                  <Button onClick={() => onComplete?.(createdLoanId!)}>
                    <FileText className="w-4 h-4 mr-2" />
                    Go to Document Checklist
                  </Button>
                  <Button variant="outline" onClick={onCancel}>
                    Close Wizard
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        );

      default:
        return null;
    }
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Progress Header */}
      <Card>
        <CardHeader>
          <CardTitle>New Loan Setup Wizard</CardTitle>
          <CardDescription>
            Step {currentStep} of {steps.length}
          </CardDescription>
          <div className="space-y-2">
            <Progress value={progress} className="h-2" />
            <div className="flex justify-between text-xs text-muted-foreground">
              {steps.map((step) => {
                const Icon = step.icon;
                return (
                  <div key={step.id} className={`flex items-center gap-1 ${currentStep >= step.id ? 'text-primary' : ''}`}>
                    <Icon className="w-3 h-3" />
                    <span>{step.title}</span>
                  </div>
                );
              })}
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Step Content */}
      {renderStepContent()}
    </div>
  );
}

