                  </FormItem>
                )}
              />
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium">Google Drive Folder (Optional)</label>
              <GoogleDriveFolderSelector
                onFolderSelected={(folderId, folderName) => {
                  setSelectedFolderId(folderId);
                  setSelectedFolderName(folderName);
                }}
                propertyAddress={form.watch("propertyAddress")}
                currentFolderId={selectedFolderId}
              />
              {selectedFolderName && (
                <p className="text-xs text-muted-foreground">
                  Selected: {selectedFolderName}
                </p>
              )}
            </div>

            <DialogFooter>
              <Button type="button" variant="outline" onClick={() => setOpen(false)}>
                Cancel
              </Button>
              <Button type="submit" disabled={loading}>
                {loading ? "Creating..." : "Create Loan"}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/SendToAnalyst.tsx.content:
import { Document, Contact } from "@/lib/types";
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Mail, FileText, Users, Send } from "lucide-react";
import { format } from "date-fns";

interface SendToAnalystProps {
  documents: Document[];
  contacts: Contact[];
  loanId: number;
  loanNumber: string;
  propertyAddress: string;
  documentAssignments?: Record<string, string[]>; // requirement name -> document IDs
  completedRequirements?: string[]; // list of completed requirement names
}

export default function SendToAnalyst({ 
  documents, 
  contacts, 
  loanId, 
  loanNumber,
  propertyAddress,
  documentAssignments = {},
  completedRequirements = []
}: SendToAnalystProps) {
  const [open, setOpen] = useState(false);
  const [selectedDocuments, setSelectedDocuments] = useState<number[]>([]);
  const [selectedAnalysts, setSelectedAnalysts] = useState<number[]>([]);
  const [customMessage, setCustomMessage] = useState("");
  const [isSending, setIsSending] = useState(false);
  const { toast } = useToast();

  // Filter analysts from contacts
  const analysts = contacts.filter(contact => 
    contact.role === "analyst" || contact.isAnalyst
  );

  const toggleDocument = (docId: number) => {
    setSelectedDocuments(prev => 
      prev.includes(docId) 
        ? prev.filter(id => id !== docId)
        : [...prev, docId]
    );
  };

  const toggleAnalyst = (contactId: number) => {
    setSelectedAnalysts(prev => 
      prev.includes(contactId) 
        ? prev.filter(id => id !== contactId)
        : [...prev, contactId]
    );
  };

  const selectAllDocuments = () => {
    setSelectedDocuments(documents.map(doc => doc.id));
  };

  const clearAllDocuments = () => {
    setSelectedDocuments([]);
  };

  const generateEmailContent = () => {
    const selectedDocs = documents.filter(doc => selectedDocuments.includes(doc.id));
    const selectedAnalystContacts = contacts.filter(contact => selectedAnalysts.includes(contact.id));

    // Create a mapping of document ID to requirement name
    const docToRequirement: Record<string, string> = {};
    Object.entries(documentAssignments).forEach(([requirementName, docIds]) => {
      docIds.forEach(docId => {
        docToRequirement[docId] = requirementName;
      });
    });

    const documentList = selectedDocs.map(doc => {
      const requirementName = docToRequirement[doc.id.toString()];
      const category = requirementName || (doc.category ? doc.category.toUpperCase() : 'DOCUMENT');
      return `‚Ä¢ ${doc.name} (${category})`;
    }).join('\n');

    // Create greeting with analyst names
    const greeting = selectedAnalystContacts.length > 1 
      ? `Dear ${selectedAnalystContacts.map(contact => contact.name.split(' ')[0]).join(' and ')},`
      : `Dear ${selectedAnalystContacts[0]?.name.split(' ')[0] || 'Analyst'},`;

    const emailContent = `${greeting}

Please review the attached documents for the loan file:

Property: ${propertyAddress}
Loan Number: ${loanNumber}
Date: ${format(new Date(), 'MMMM dd, yyyy')}

Documents attached (${selectedDocs.length}):
${documentList}

${customMessage ? `Additional Notes:\n${customMessage}\n` : ''}
Please review and let us know if you need any additional documentation.

Best regards,
Daniel Adler
    `.trim();

    return {
      content: emailContent,
      recipients: selectedAnalystContacts.map(contact => contact.email).filter(Boolean),
      documentIds: selectedDocuments
    };
  };

  const sendToAnalyst = async () => {
    if (selectedDocuments.length === 0) {
      toast({
        title: "Error",
        description: "Please select at least one document to send.",
        variant: "destructive"
      });
      return;
    }

    if (selectedAnalysts.length === 0) {
      toast({
        title: "Error", 
        description: "Please select at least one analyst to send to.",
        variant: "destructive"
      });
      return;
    }

    setIsSending(true);
    try {
      const emailData = generateEmailContent();

      const response = await apiRequest("POST", `/api/loans/${loanId}/send-to-analyst`, {
        documentIds: selectedDocuments,
        analystIds: selectedAnalysts,
        customMessage,
        emailContent: emailData.content,
        recipients: emailData.recipients
      });

      toast({
        title: "Success",
        description: `Email drafted and ready to send to ${selectedAnalysts.length} analyst(s) with ${selectedDocuments.length} document(s).`
      });

      // Show follow-up call reminder for analyst communications
      setTimeout(() => {
        const analystNames = selectedAnalysts.map(id => 
          analysts.find(a => a.id === id)?.name
        ).filter(Boolean).join(", ");

        toast({
          title: "üìû Follow-up Reminder",
          description: `After sending to analysts (${analystNames}), call them to confirm receipt and discuss timeline.`,
          duration: 8000,
        });
      }, 1500);

      setOpen(false);
      setSelectedDocuments([]);
      setSelectedAnalysts([]);
      setCustomMessage("");
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to prepare email for analysts.",
        variant: "destructive"
      });
    } finally {
      setIsSending(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" className="gap-2">
          <Mail className="w-4 h-4" />
          Send to Analyst
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Send className="w-5 h-5" />
            Send Documents to Analyst
          </DialogTitle>
          <DialogDescription>
            Select documents and analysts to create an email package for review.
          </DialogDescription>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Document Selection */}
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <Label className="text-sm font-medium flex items-center gap-2">
                <FileText className="w-4 h-4" />
                Select Documents ({selectedDocuments.length}/{documents.length})
              </Label>
              <div className="flex gap-2">
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  onClick={selectAllDocuments}
                >
                  Select All
                </Button>
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  onClick={clearAllDocuments}
                >
                  Clear All
                </Button>
              </div>
            </div>

            <div className="max-h-64 overflow-y-auto space-y-2 border rounded-md p-3">
              {documents.length === 0 ? (
                <p className="text-sm text-gray-500 text-center py-4">
                  No documents available
                </p>
              ) : (
                documents.map((doc) => {
                  // Check if this document is associated with any completed requirements
                  const isCompleted = documentAssignments && Object.entries(documentAssignments).some(([requirement, docIds]) => 
                    docIds.includes(doc.id.toString()) && completedRequirements?.includes(requirement)
                  );

                  return (
                    <div 
                      key={doc.id} 
                      className={`flex items-center space-x-3 p-2 rounded border-l-4 ${
                        isCompleted 
                          ? 'bg-green-50 border-l-green-500 hover:bg-green-100' 
                          : 'border-l-transparent hover:bg-gray-50'
                      }`}
                    >
                      <Checkbox
                        id={`doc-${doc.id}`}
                        checked={selectedDocuments.includes(doc.id)}
                        onCheckedChange={() => toggleDocument(doc.id)}
                      />
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2">
                          <label 
                            htmlFor={`doc-${doc.id}`}
                            className={`text-sm font-medium cursor-pointer truncate block ${
                              isCompleted ? 'text-green-800' : ''
                            }`}
                          >
                            {doc.name}
                          </label>
                          {isCompleted && (
                            <Badge className="bg-green-100 text-green-800 border-green-300 text-xs px-2 py-0.5">
                              ‚úì Complete
                            </Badge>
                          )}
                        </div>
                        <div className="flex items-center gap-2 mt-1">
                          {doc.category && (
                            <Badge variant="outline" className="text-xs">
                              {doc.category}
                            </Badge>
                          )}
                          {doc.fileSize && (
                            <span className="text-xs text-gray-500">
                              {(doc.fileSize / 1024 / 1024).toFixed(1)} MB
                            </span>
                          )}
                        </div>
                      </div>
                    </div>
                  );
                })
              )}
            </div>
          </div>

          {/* Analyst Selection */}
          <div className="space-y-4">
            <Label className="text-sm font-medium flex items-center gap-2">
              <Users className="w-4 h-4" />
              Select Analysts ({selectedAnalysts.length}/{analysts.length})
            </Label>

            <div className="max-h-64 overflow-y-auto space-y-2 border rounded-md p-3">
              {analysts.length === 0 ? (
                <p className="text-sm text-gray-500 text-center py-4">
                  No analysts assigned to this loan.
                  <br />
                  Add analysts in the Contacts section.
                </p>
              ) : (
                analysts.map((analyst) => (
                  <div key={analyst.id} className="flex items-center space-x-3 p-2 hover:bg-gray-50 rounded">
                    <Checkbox
                      id={`analyst-${analyst.id}`}
                      checked={selectedAnalysts.includes(analyst.id)}
                      onCheckedChange={() => toggleAnalyst(analyst.id)}
                    />
                    <div className="flex-1">
                      <label 
                        htmlFor={`analyst-${analyst.id}`}
                        className="text-sm font-medium cursor-pointer block"
                      >
                        {analyst.name}
                      </label>
                      {analyst.email && (
                        <p className="text-xs text-gray-500">{analyst.email}</p>
                      )}
                      {analyst.company && (
                        <p className="text-xs text-gray-400">{analyst.company}</p>
                      )}
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>

        {/* Custom Message */}
        <div className="space-y-2">
          <Label htmlFor="custom-message" className="text-sm font-medium">
            Additional Notes (Optional)
          </Label>
          <Textarea
            id="custom-message"
            placeholder="Add any additional notes or instructions for the analysts..."
            value={customMessage}
            onChange={(e) => setCustomMessage(e.target.value)}
            rows={3}
          />
        </div>

        {/* Email Preview */}
        {selectedDocuments.length > 0 && selectedAnalysts.length > 0 && (
          <Card>
            <CardContent className="p-4">
              <Label className="text-sm font-medium mb-2 block">Email Preview:</Label>
              <div className="bg-gray-50 p-3 rounded text-sm whitespace-pre-line max-h-32 overflow-y-auto">
                {generateEmailContent().content}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Actions */}
        <div className="flex justify-end gap-2 pt-4 border-t">
          <Button
            variant="outline"
            onClick={() => setOpen(false)}
          >
            Cancel
          </Button>
          <Button
            onClick={sendToAnalyst}
            disabled={isSending || selectedDocuments.length === 0 || selectedAnalysts.length === 0}
            className="gap-2"
          >
            {isSending ? (
              "Preparing..."
            ) : (
              <>
                <Mail className="w-4 h-4" />
                Draft Email ({selectedDocuments.length} docs)
              </>
            )}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/SmartDocumentUploa.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Upload, FileText, Image, File } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";

interface SmartDocumentUploadProps {
  loanId: number;
  onSuccess?: () => void;
}

const documentCategories = [
  { value: "borrower", label: "Borrower Documents" },
  { value: "title", label: "Title Documents" },
  { value: "insurance", label: "Insurance Documents" },
  { value: "current_lender", label: "Current Lender Documents" },
  { value: "other", label: "Other Documents" }
];

const commonDocumentTypes = [
  "Driver's License",
  "Articles of Organization",
  "Operating Agreement",
  "Bank Statements",
  "Voided Check",
  "Property Deed",
  "Title Report",
  "Insurance Policy",
  "Property Tax Bill",
  "Rent Roll",
  "Lease Agreement",
  "Appraisal Report",
  "Other"
];

export default function SmartDocumentUpload({ loanId, onSuccess }: SmartDocumentUploadProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [documentTitle, setDocumentTitle] = useState("");
  const [documentType, setDocumentType] = useState("");
  const [category, setCategory] = useState("");
  const [notes, setNotes] = useState("");
  const [isUploading, setIsUploading] = useState(false);

  const { toast } = useToast();

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      // Auto-suggest title based on filename (without extension)
      const nameWithoutExt = file.name.split('.').slice(0, -1).join('.');
      setDocumentTitle(nameWithoutExt.replace(/[-_]/g, ' '));
    }
  };

  const handleUpload = async () => {
    if (!selectedFile || !documentTitle.trim() || !category) {
      toast({
        title: "Missing Information",
        description: "Please select a file, enter a document title, and choose a category.",
        variant: "destructive"
      });
      return;
    }

    setIsUploading(true);
    try {
      const formData = new FormData();
      formData.append('file', selectedFile);
      formData.append('name', documentTitle.trim());
      formData.append('category', category);
      if (documentType && documentType !== 'Other') {
        formData.append('type', documentType);
      }
      if (notes.trim()) {
        formData.append('notes', notes.trim());
      }

      const response = await fetch(`/api/loans/${loanId}/documents`, {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();

      // Refresh the loan data
      queryClient.invalidateQueries({ queryKey: [`/api/loans/${loanId}`] });

      toast({
        title: "Document Uploaded",
        description: `"${documentTitle}" has been successfully uploaded.`
      });

      // Reset form
      setSelectedFile(null);
      setDocumentTitle("");
      setDocumentType("");
      setCategory("");
      setNotes("");
      setIsOpen(false);

      if (onSuccess) {
        onSuccess();
      }
    } catch (error) {
      console.error('Upload error:', error);
      toast({
        title: "Upload Failed",
        description: "There was an error uploading your document. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsUploading(false);
    }
  };

  const getFileIcon = (file: File) => {
    if (file.type.startsWith('image/')) {
      return <Image className="w-8 h-8 text-blue-500" />;
    } else if (file.type === 'application/pdf') {
      return <FileText className="w-8 h-8 text-red-500" />;
    } else {
      return <File className="w-8 h-8 text-gray-500" />;
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" className="w-full">
          <Upload className="w-4 h-4 mr-2" />
          Upload Document
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Upload New Document</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* File Selection */}
          <div>
            <Label htmlFor="file-upload">Select File</Label>
            <div className="mt-2">
              <input
                id="file-upload"
                type="file"
                accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
                onChange={handleFileSelect}
                className="hidden"
              />
              <Button
                variant="outline"
                onClick={() => document.getElementById('file-upload')?.click()}
                className="w-full h-20 border-dashed border-2 flex flex-col items-center justify-center"
              >
                {selectedFile ? (
                  <>
                    {getFileIcon(selectedFile)}
                    <span className="text-sm font-medium mt-1">{selectedFile.name}</span>
                    <span className="text-xs text-muted-foreground">
                      {(selectedFile.size / 1024).toFixed(1)} KB
                    </span>
                  </>
                ) : (
                  <>
                    <Upload className="w-8 h-8 text-muted-foreground" />
                    <span className="text-sm text-muted-foreground mt-1">
                      Click to select a file
                    </span>
                  </>
                )}
              </Button>
            </div>
          </div>

          {/* Document Title */}
          <div>
            <Label htmlFor="document-title">Document Title *</Label>
            <Input
              id="document-title"
              value={documentTitle}
              onChange={(e) => setDocumentTitle(e.target.value)}
              placeholder="e.g., Driver's License - John Smith"
              className="mt-1"
            />
          </div>

          {/* Document Type */}
          <div>
            <Label htmlFor="document-type">Document Type</Label>
            <Select value={documentType} onValueChange={setDocumentType}>
              <SelectTrigger className="mt-1">
                <SelectValue placeholder="Select document type (optional)" />
              </SelectTrigger>
              <SelectContent>
                {commonDocumentTypes.map((type) => (
                  <SelectItem key={type} value={type}>
                    {type}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Category */}
          <div>
            <Label htmlFor="category">Category *</Label>
            <Select value={category} onValueChange={setCategory}>
              <SelectTrigger className="mt-1">
                <SelectValue placeholder="Select category" />
              </SelectTrigger>
              <SelectContent>
                {documentCategories.map((cat) => (
                  <SelectItem key={cat.value} value={cat.value}>
                    {cat.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Notes */}
          <div>
            <Label htmlFor="notes">Notes (optional)</Label>
            <Textarea
              id="notes"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Add any additional notes about this document..."
              className="mt-1"
              rows={2}
            />
          </div>

          {/* Upload Button */}
          <div className="flex gap-3 pt-4">
            <Button
              variant="outline"
              onClick={() => setIsOpen(false)}
              className="flex-1"
            >
              Cancel
            </Button>
            <Button
              onClick={handleUpload}
              disabled={!selectedFile || !documentTitle.trim() || !category || isUploading}
              className="flex-1"
            >
              {isUploading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-t-2 border-b-2 border-current mr-2"></div>
                  Uploading...
                </>
              ) : (
                <>
                  <Upload className="w-4 h-4 mr-2" />
                  Upload
                </>
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/TaskList.tsx.content:
import { Task } from "@/lib/types";
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { format } from "date-fns";

interface TaskListProps {
  tasks: Task[];
  loanId: number;
}

const taskSchema = z.object({
  description: z.string().min(1, "Task description is required"),
  priority: z.string().min(1, "Priority is required"),
  dueDate: z.string().optional(),
  completed: z.boolean().default(false)
});

export default function TaskList({ tasks, loanId }: TaskListProps) {
  const [isAddTaskOpen, setIsAddTaskOpen] = useState(false);
  const { toast } = useToast();

  const form = useForm<z.infer<typeof taskSchema>>({
    resolver: zodResolver(taskSchema),
    defaultValues: {
      description: "",
      priority: "medium",
      dueDate: format(new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), 'yyyy-MM-dd'), // Default to 1 week from now
      completed: false
    }
  });

  const onSubmit = async (data: z.infer<typeof taskSchema>) => {
    try {
      await apiRequest("POST", `/api/loans/${loanId}/tasks`, data);
      queryClient.invalidateQueries({ queryKey: [`/api/loans/${loanId}`] });
      setIsAddTaskOpen(false);
      form.reset();
      toast({
        title: "Task added",
        description: "The task has been added successfully."
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add task. Please try again.",
        variant: "destructive"
      });
    }
  };

  const toggleTaskCompletion = async (taskId: number, completed: boolean) => {
    try {
      await apiRequest("PATCH", `/api/tasks/${taskId}`, { completed });
      queryClient.invalidateQueries({ queryKey: [`/api/loans/${loanId}`] });

      // Auto-open the "Add task" dialog when a task is completed
      if (completed) {
        setTimeout(() => {
          setIsAddTaskOpen(true);
        }, 500); // Small delay to let the UI update first
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update task. Please try again.",
        variant: "destructive"
      });
    }
  };

  // Sort tasks - incomplete tasks first, then by due date
  const sortedTasks = [...tasks].sort((a, b) => {
    // First by completion status
    if (a.completed !== b.completed) {
      return a.completed ? 1 : -1;
    }

    // Then by priority
    const priorityOrder = { high: 0, medium: 1, low: 2 };
    const aPriority = priorityOrder[a.priority as keyof typeof priorityOrder] || 1;
    const bPriority = priorityOrder[b.priority as keyof typeof priorityOrder] || 1;

    if (aPriority !== bPriority) {
      return aPriority - bPriority;
    }

    // Then by due date
    if (a.dueDate && b.dueDate) {
      return new Date(a.dueDate).getTime() - new Date(b.dueDate).getTime();
    }

    return 0;
  });

  return (
    <>
      <div className="bg-white rounded-lg shadow-sm border border-gray-100" data-component="task-list">
        <div className="px-4 py-5 sm:px-6 border-b border-gray-100 flex justify-between items-center">
          <div>
            <h3 className="text-lg leading-6 font-heading font-medium text-gray-900 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-amber-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
              </svg>
              Action Items
            </h3>
            <p className="mt-1 max-w-2xl text-sm text-gray-500">
              Tasks to complete for this loan file
            </p>
          </div>
          <Button 
            onClick={() => setIsAddTaskOpen(true)}
            variant="outline"
            className="border-amber-300 text-amber-600 hover:bg-amber-50"
            size="sm"
          >
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-4 h-4 mr-1">
              <line x1="12" y1="5" x2="12" y2="19"></line>
              <line x1="5" y1="12" x2="19" y2="12"></line>
            </svg>
            Add task
          </Button>
        </div>
        <div className="px-4 py-1">
          <div className="divide-y divide-gray-100">
            {sortedTasks.map((task) => (
              <div key={task.id} className="py-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <Checkbox 
                      id={`task-${task.id}`}
                      checked={task.completed}
                      onCheckedChange={(checked) => toggleTaskCompletion(task.id, checked as boolean)}
                      className={`h-4 w-4 rounded ${task.priority === 'high' ? 'text-red-500' : 'text-blue-500'}`}
                    />
                    <label 
                      htmlFor={`task-${task.id}`} 
                      className={`ml-3 block text-sm font-medium ${task.completed ? 'text-gray-400 line-through' : 'text-gray-700'}`}
                    >
                      {task.description}
                      {task.priority === 'high' && 
                        <span className="ml-2 inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-red-100 text-red-800">
                          High Priority
                        </span>
                      }
                      {task.priority === 'medium' && 
                        <span className="ml-2 inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-yellow-100 text-yellow-800">
                          Medium
                        </span>
                      }
                    </label>
                  </div>
                  <div className="ml-4 flex-shrink-0 flex">
                    <span className={`text-xs rounded-full px-2 py-1 ${
                      task.completed 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-blue-100 text-blue-800'
                    }`}>
                      {task.completed 
                        ? `Completed ${task.dueDate ? format(new Date(task.dueDate), 'MMM d') : ''}` 
                        : `Due ${task.dueDate ? format(new Date(task.dueDate), 'MMM d') : 'soon'}`}
                    </span>
                  </div>
                </div>
              </div>
            ))}

            {tasks.length === 0 && (
              <div className="py-6 text-center text-gray-500 text-sm">
                No tasks added yet
              </div>
            )}
          </div>
        </div>
        <div className="px-4 py-3 bg-gray-50 sm:px-6 border-t border-gray-100 flex justify-between items-center">
          <span className="text-sm text-gray-500 flex items-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1 text-green-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
            </svg>
            {tasks.filter(task => task.completed).length} of {tasks.length} tasks completed
          </span>
        </div>
      </div>

      {/* Add Task Dialog */}
      <Dialog open={isAddTaskOpen} onOpenChange={setIsAddTaskOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add Task</DialogTitle>
          </DialogHeader>

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Task Description</FormLabel>
                    <FormControl>
                      <Input placeholder="Contact insurance agent for binder" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="priority"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Priority</FormLabel>
                    <Select 
                      onValueChange={field.onChange} 
                      defaultValue={field.value}
                    >
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Select priority" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="high">High</SelectItem>
                        <SelectItem value="medium">Medium</SelectItem>
                        <SelectItem value="low">Low</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="dueDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Due Date</FormLabel>
                    <FormControl>
                      <Input type="date" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <DialogFooter>
                <Button variant="outline" type="button" onClick={() => setIsAddTaskOpen(false)}>
                  Cancel
                </Button>
                <Button type="submit">Add Task</Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </>
  );
}

