  completionPercentage?: number;
  createdAt?: Date;
  updatedAt?: Date;
}

export interface Document {
  id: number;
  name: string;
  fileId: string; // Google Drive file ID
  fileType?: string;
  fileSize?: number;
  category?: string; // borrower, property, title, insurance, etc.
  source?: string; // upload, gmail, drive
